mapper.select:
select page_2.* from (select page_1.*, rownum page_num from (
select * from ( select SYSUSER.id, SYSUSER.userid, SYSUSER.username, SYSUSER.groupid, sysuser.USERSTATE, SYSUSER.sysid, decode(SYSUSER.Groupid, '1', (select mc from userjd where id = sysuser.sysid), '2', (select mc from userjd where id = sysuser.sysid), '3', (select mc from useryy where id = sysuser.sysid), '4', (select mc from usergys where id = sysuser.sysid) ) sysmc
from SYSUSER )sysuser
) page_1
where rownum <= ${pageQuery.PageQuery_end}) page_2 where page_2.page_num >= ${pageQuery.PageQuery_start}
